import{_ as o,c as t,o as r,a1 as i,j as e,a}from"./chunks/framework.BtoQSCtf.js";const s="/Tile-Tech/assets/5vsm-actual.CM0BwXbq.png",n="/Tile-Tech/assets/1plant-simulation.inpdh3aP.png",k=JSON.parse('{"title":"Gestion de producción","description":"","frontmatter":{},"headers":[],"relativePath":"producto/5-gestion-produccion.md","filePath":"producto/5-gestion-produccion.md"}'),d={name:"producto/5-gestion-produccion.md"},c=i('<h1 id="gestion-de-produccion" tabindex="-1">Gestion de producción <a class="header-anchor" href="#gestion-de-produccion" aria-label="Permalink to &quot;Gestion de producción&quot;">​</a></h1><p>El analisis de la gestion de producción sera realizado utilizando la herramienta de tecnomatix, la cual permite simular el proceso de produccion de la empresa antes y despues de la intervención.</p><h2 id="vsm-value-stream-mapping" tabindex="-1">VSM (Value Stream Mapping) <a class="header-anchor" href="#vsm-value-stream-mapping" aria-label="Permalink to &quot;VSM (Value Stream Mapping)&quot;">​</a></h2><p>Basado en la información anterior se realizó un mapa de flujo de valor para identificar los procesos con mayor potencial de mejora y candidatos a ser automatizados. Para esto se utilizo la herramienta de tecnomatix.</p><p><img src="'+s+'" alt="VSM"></p><p><img src="'+n+'" alt="teconmatix"></p><h2 id="cuellos-de-botella-y-areas-de-mejora" tabindex="-1">Cuellos de botella y áreas de mejora <a class="header-anchor" href="#cuellos-de-botella-y-areas-de-mejora" aria-label="Permalink to &quot;Cuellos de botella y áreas de mejora&quot;">​</a></h2><blockquote><p>Entre las 588 muestras obtenidas, los defectos incluyen tipos de grietas (60,9%), esquinas rotas (9,9%), defectos de impresión y decoración (9,4%), bordes rotos (8%), defectos en el esmaltado inferior (5,4%), sombras ( 3,9%) y otros casos (2,6%). Teniendo en cuenta las cuestiones anteriores, se analizó el parámetro de tipos de grietas como la principal causa de creación de defectos en las baldosas, que se asoció con las máquinas de prensado</p></blockquote><h2 id="indicadores-claves-de-diseno" tabindex="-1">Indicadores claves de diseño <a class="header-anchor" href="#indicadores-claves-de-diseno" aria-label="Permalink to &quot;Indicadores claves de diseño&quot;">​</a></h2><embed src="/Tile-Tech/KPI.pdf" type="application/pdf" width="100%" height="600px"><p><a href="/Tile-Tech/KPI.pdf">KPI</a></p><h2 id="reporte-de-tecnomatix-previo-a-intervencion" tabindex="-1">Reporte de tecnomatix previo a intervención <a class="header-anchor" href="#reporte-de-tecnomatix-previo-a-intervencion" aria-label="Permalink to &quot;Reporte de tecnomatix previo a intervención&quot;">​</a></h2>',12),l=e("iframe",{src:"tecnomatix_report.html",width:"%80",height:"400",frameborder:"0",allowfullscreen:""},null,-1),p=e("h2",{id:"reporte-de-tecnomatix-posterior-a-intervencion",tabindex:"-1"},[a("Reporte de tecnomatix posterior a intervención "),e("a",{class:"header-anchor",href:"#reporte-de-tecnomatix-posterior-a-intervencion","aria-label":'Permalink to "Reporte de tecnomatix posterior a intervención"'},"​")],-1),m=e("iframe",{src:"tecnomatix_report.html",width:"%80",height:"400",frameborder:"0",allowfullscreen:""},null,-1),u=e("h2",{id:"modelos-usado-en-el-diseno",tabindex:"-1"},[a("Modelos usado en el diseño "),e("a",{class:"header-anchor",href:"#modelos-usado-en-el-diseno","aria-label":'Permalink to "Modelos usado en el diseño"'},"​")],-1),h=e("ul",null,[e("li",null,[a("caja de empaque tomado de "),e("a",{href:"https://grabcad.com/library/cardboard-insert-1",target:"_blank",rel:"noreferrer"},"Cardboard Insert")])],-1),_=[c,l,p,m,u,h];function f(b,v,x,g,q,P){return r(),t("div",null,_)}const y=o(d,[["render",f]]);export{k as __pageData,y as default};
